using Blogifier.Core.Providers;
using Microsoft.Extensions.Configuration;
using System.Collections.Generic;
using System.Threading.Tasks;
using Xunit;

namespace Blogifier.Tests
{
	public class StorageProviderTests
	{
      private readonly string _imgUrl = "https://user-images.githubusercontent.com/1932785/81506457-1611e580-92bc-11ea-927e-b826c56ba21b.png";
      private readonly string _img64 = "<img src=\"data:image/gif;base64,\">";

      [Fact]
      public async Task CanUploadImageOrFileFromUrl()
      {
         var sut = GetSut();

         var result = await sut.UploadFromWeb(new System.Uri(_imgUrl), "/", "1/2020/12");

         Assert.True(result.Length > 0);
      }

      [Fact]
      public async Task CanUploadBase64Image()
      {
         var sut = GetSut();

         var result = await sut.UploadBase64Image(_img64, "/", "1/2020/12");

         Assert.True(result.Length > 0);
      }

      IStorageProvider GetSut()
      {
         var inMemorySettings = new Dictionary<string, string> {
            {"Blgofier:FileExtensions", "png,gif,jpeg,jpg,zip"}
         };

         IConfiguration configuration = new ConfigurationBuilder()
            .AddInMemoryCollection(inMemorySettings)
            .Build();

         return new StorageProvider(configuration);
      }
   }
}
